    csv_file_path = '../Data/custom_url.csv'
    examples = []

    @classmethod
    def load_data(cls):
        with open(cls.csv_file_path, 'r', newline='', encoding='utf-8') as csvfile:
            csvreader = csv.DictReader(csvfile)
            for row in csvreader:
                site = row['Site']
                link = row['Link']
                cls.examples.append([site, link])

